mapper.select:
SELECT c.*, t1.from_user_name, t1.from_user_head FROM COMMENT c, ( SELECT c.id, u.username from_user_name, u.head_url from_user_head FROM COMMENT c LEFT JOIN sys_user u ON c.from_user_id = u.user_id ) t1
t1.id = c.id
AND c.id = #{id}
AND t1.from_user_name LIKE CONCAT('%', CONCAT(#{fromUserName}, '%'))
AND c.content LIKE CONCAT('%', CONCAT(#{content}, '%'))
AND c.create_time>#{createTimeStart}
AND #{createTimeEnd}>c.create_time
AND c.`status` = #{status}
order by c.create_time

mapper.update:
update comment
set like_count=like_count + 1
where id = #{id}
